  const [featVillager, setFeatVillager] = useState([]);

  function setFeat(id){
    let choice = props.villagers.find(v => (
      v.id ===id)
    );
    setFeatVillager(choice);
  }

    return (
      <div className="characterPage">
        {/* <FeaturedCharacter featVillager ={featVillager}/> */}
        <CharacterGrid villagers={props.villagers} selectFeat={(id) = setFeat(id)}/>
      </div>
    );
  }

  export default CharacterPage;
