import React, { useEffect, useState } from "react";
import "./FeatCharacter.css";



function FeaturedCharacter(props) {


    function writeList(list){
        console.log(list)
        let faves = list.join(', ');
        return faves;
    }

    function generateHearts(hearts){
      let friendship =[];
      if (hearts === 0){
        return friendship;
      }
      else {
        friendship.push({'img': '<3 '})
        hearts--;
        return friendship + generateHearts(hearts)
      }
    }

    return (
      <div className="FeaturedCharacter">
          {props.featVillager.img && 
          <table>  
              <tbody>
                  <td>
                    <img src ={props.featVillager.img}/>
                  </td>
                  <td>
                    <h2>{props.featVillager.name}</h2>
                    <div id='x'><li>Friendship Level: {props.featVillager.hearts && generateHearts(props.featVillager.hearts).map(n=>n)}</li></div>
                    <li>Birthday: {props.featVillager.birthday}</li>
                    <li>Favourite Gifts: {props.featVillager.favGifts && writeList(props.featVillager.favGifts)}</li>
                  </td>
              </tbody>
          </table>
          }
      </div>
    );
  }

  export default FeaturedCharacter;
