function generateHearts(n){
    let friendship = new Array;
    for (let i = 0; i<n;i++){
        friendship.push(' ');
    }

    function heartImage(){
        return <img src = 'heart.png'></img>
    }


return(

<p>Friendship Level: {props.hearts && friendship.forEach(n => heartImage(n))}</p>

)

}

}

export default Friendship;
